for item in _input.all():
  for key, value in item.json.items():
    if key=='id':
      continue
    new_key = '_'+key
    item.json[new_key] = value
    del item.json[key]
  item.json._job_link = f"https://www.upwork.com/jobs/~02{item.json.id}"
return _input.all()

for item in _input.all():
  hour_max = item.json.hourly_budget_max
  fixed_price = float(item.json.amount_raw_value)
  keep = False
  if hour_max:
    if hour_max >= 25:
      keep = True
  elif fixed_price >= 5:
    keep = True
  item.json.keep = keep
return _input.all()
