# Loop over input items and add a new field called 'myNewField' to the JSON of each one
for item in _input.all():
  score = 0
  for key, value in item.json.output.items():
    if key != 'rationale':
      score += value
  item.json.score = score
  item.json.rationale = item.json.output.rationale
  del item.json.output
return _input.all()

# Loop over input items and add a new field called 'myNewField' to the JSON of each one
for item in _input.all():
  for key, value in item.json.items():
    if key=='id':
      continue
    new_key = '_'+key
    item.json[new_key] = value
    del item.json[key]
  item.json._job_link = f"https://www.upwork.com/jobs/~02{item.json.id}"
return _input.all()

